using namespace std;

int main()
{
    string word;
    string line;
    int count = 0;
    ifstream myfile;

    cout << "Enter word to count: ";
    cin >> word;

    myfile.open("file.txt");  // open the text file

    if (myfile.is_open())
    {
        while (getline(myfile, line))
        {
            if (line.find(word) != string::npos)  // check if the line contains the word
