    def __init__(self):
        self.stack1 = []
        self.stack2 = []

    def enqueue(self, data):
        self.stack1.append(data)

    def dequeue(self):
        if len(self.stack2) == 0:
            if len(self.stack1) == 0:
                return None
            while len(self.stack1) > 0:
                temp = self.stack1.pop()
                self.stack2.append(temp)
        return self.stack2.pop()

